answer.css:
.bold-text {
    font-weight: bold;
}

.italic-text {
    font-style: italic;
}

#main-container {
    width: 80%;
    margin: 0 auto;
}

#content {
    float: left;
    width: 65%;
}

#sidebar {
    float: right;
    width: 30%;
}

#sidebar form {
    margin-top: 20px;
}

#sidebar form div {
    margin-bottom: 15px;
}

#sidebar input[type="text"],
#sidebar input[type="email"] {
    width: 100%;
    padding: 8px;
    border: 1px solid #ddd;
    border-radius: 4px;
    box-sizing: border-box;
}

#sidebar button {
    width: 100%;
    padding: 10px;
    background-color: #4CAF50;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
    font-size: 16px;
}

#sidebar button:hover {
    background-color: #45a049;
}

h1, h2, h3 {
    color: #333;
}

ul {
    line-height: 1.6;
}

p {
    line-height: 1.6;
    color: #666;
}

